mapper.select:
SELECT xSpot,ySpot,x1Spot,y1Spot 
from area 
where floorNo = #{value}

SELECT xSpot,ySpot,x1Spot,y1Spot 
from area 
where id = #{value}

SELECT xSpot,ySpot,x1Spot,y1Spot,areaName,floorNo,id 
from area 
where placeId = #{value}

mapper.delete:
DELETE 
FROM area 
where 
    xSpot = #{xSpot} 
    and ySpot = #{ySpot} 
    and x1Spot = #{x1Spot} 
    and y1Spot = #{y1Spot} 
    and floorNo = #{maps.floorNo} 
    and categoryid = #{category.id}

mapper.select:
SELECT count(distinct userID) number
FROM ${tableName}
where 
    timestamp > #{startTime}
    and timestamp < #{nowTime}
    and z = #{area.maps.floorNo} 
    and x > (#{area.xSpot}*10) 
    and x < (#{area.x1Spot}*10) 
    and y > (#{area.ySpot}*10) 
    and y < (#{area.y1Spot}*10)

SELECT count(distinct userID) number
FROM ${tableName}
where 
    z = #{floorNo}

