mapper.select:
SELECT a.*,b.* 
FROM locationphone a left join maps b on a.mapId = b.mapId 
WHERE a.userID = #{userId} and a.mapId = #{mapId}

SELECT * 
FROM (
    SELECT * 
    FROM ${tableName}
    WHERE mapId = #{mapId}
        and timestamp > #{time}
    ORDER BY timestamp desc
) a 
GROUP BY a.userID

SELECT * 
FROM (
    SELECT * 
    FROM ${tableName}
    WHERE mapId = #{mapId} 
) a 
GROUP BY a.userID

SELECT locationphone.* 
FROM locationphone 
WHERE userID = #{value}
    and timestamp = (
        SELECT max(timestamp) 
        FROM locationphone 
        WHERE userID = #{userId}
    )

SELECT distinct(userID) 
FROM ${tableName}
WHERE mapId = #{mapId} 
    and timestamp > #{time}

SELECT * 
FROM ${tableName}
WHERE userID = #{userId} and timestamp > #{time}
ORDER BY timestamp

select.foreach:
SELECT userID 
FROM location${time} 
WHERE mapId = #{mapId} and idType = 'MSISDN'

mapper.select:
SELECT a.*,r.role,r.userName,r.phoneNumber 
FROM (
    SELECT * FROM (
        SELECT * FROM ${tableName}
        WHERE mapId = #{mapId} and timestamp > #{time} 
        ORDER BY timestamp desc
    ) a 
GROUP BY a.userID
) a left join register r on a.userID = r.userId

SELECT count(distinct a.userID) number 
FROM ${tableName} a join maps b on a.mapId = b.mapId 
WHERE b.placeId = #{placeId}

SELECT count(distinct a.userID) number 
FROM ${tableName} a join maps b on a.mapId = b.mapId 
WHERE b.placeId = #{placeId} and a.timestamp < #{time}

SELECT count(distinct a.userID) number 
FROM ${tableName} a join maps b on a.mapId = b.mapId
WHERE b.placeId = #{placeId} and a.timestamp > #{time}
